mapper.update:
UPDATE sys_role SET
    update_by = #{updateBy},
    update_date = #{updateDate},
    del_flag = #{DEL_FLAG_DELETE}


        WHERE id = #{id}


        WHERE company_id = #{company.id}


UPDATE sys_role



            when id=#{entity.id} then #{entity.company.id}




            when id=#{entity.id} then #{entity.code}




